  height: 100%;

  position: fixed;
  top: 0;
  left: 0;
  z-index: -2;

  background: url("${({ url }) => url}") no-repeat center;
  background-size: cover;
`;

const Background = ({ children, style, url, color }) => {
  return <StyledBackground url={url} c ></StyledBackground>;
};

export default Background;
