import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`

 @font-face {
   font-family: "Space Mono";
   font-weight: reg;
   src: url("fonts/");
 }

  * {
    margin: 0;
    padding: 0;

  }

  html {
    height:100%;
    font-family: 'Open Sans', sans-serif;

  }
`;

export default GlobalStyle;
