`;

const Bar = ({
  style,
  name,
  value,
  maxValue,
  fillColor,
  alignLeft,
  alignRight,
}) => {
  return (
    <Container style={style}>
      <Name alignLeft={alignLeft} alignRight={alignRight}>
        {name}
      </Name>
      <BarContainer alignLeft={alignLeft} alignRight={alignRight}>
        <BarContainerFill
          value={value}
          maxValue={maxValue}
          fillColor={fillColor}
        />
      </BarContainer>
    </Container>
  );
};

export default Bar;
