/* imports */
import { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useRouter } from 'next/router';
/* components */
import Container from '../../../Layout/Container';
import Header from '../Header';
import Table from '../Table';
import MarginBlock from '../../../Layout/MarginBlock';
/* redux */
import { useDispatch, useSelector } from 'react-redux';
import {
  showModalSettingsSpaceShip,
  hideModalSettingsSpaceShip,
} from '../../../../redux/actions/Modals';
import {
  setYouIsCreatorRoom,
  writeRoomId,
} from '../../../../redux/actions/Room';
/* socketio */
import { socket } from '../../../Socket';

const StyledRooms = styled.div`
  position: relative;

  width: 100%;
  height: 60%;
  border-bottom: 1px solid #fff;

  /* overflow: auto; */
`;
const TableContainer = styled.div`
  margin: 0 auto;
  width: 80%;
  @media (max-width: 1350px) {
    width: 90%;
  }
`;
const Button = styled.button`
  position: absolute;
  right: 28px;
  bottom: 22px;

  background: rgba(255, 255, 255, 0.1);

  width: 200px;
  height: 45px;
  border: 2px solid #ffffff;
  border-radius: 8px;

  text-transform: uppercase;
  font-size: 16px;
`;
const Rooms = ({}) => {
  const dataPrimary = [
    {
      data: 'Players',
      center: true,
    },
    {
      data: 'Mode',
    },
    {
      data: 'User',
    },
    {
      data: 'Message',
    },
    {
      data: 'test',
    },
  ];
  // const rowsData = [
  //   [
  //     {
  //       data: '1/20',
  //       center: true,
  //     },
  //     {
  //       data: 'Infinity waves',
  //     },
  //     {
  //       data: 'CATDEV',
  //     },
  //     {
  //       data: 'Заходите все сюда!!!!! Noobs only!',
  //     },
  //     {
  //       button: true,
  //       data: 'Join',
  //       center: true,
  //       callback: (id) => {
  //         console.log(id);
  //       },
  //     },
  //   ],
  // ];

  // const [rowsData, setRowsData] = useState();

  // {
  //   id: 'K2wAVlTot',
  //   creator: {
  //     socketId: '6nBGesAp_aX91DyIAAAw',
  //     user: {
  //       userName: 'фыва',
  //       userColor: '#0049ff'
  //     }
  //   },
  //   activated: true,
  //   configs: {
  //     limitPlayers: 'inf',
  //     message: 'фывафыва',
  //     mode: 'INFINITY'
  //   },
  //   users: []
  // }

  const rooms = useSelector((state) => state.rooms.rooms);
  const currentRoomId = useSelector((state) => state.room.currentRoom.id);
  console.log(currentRoomId);
  const rowsData =
    rooms &&
    rooms.map(
      ({
        id,
        creator: {
          user: { userName, userColor },
        },
        configs: { limitPlayers, mode, message },
      }) => {
        return [
          {
            data: limitPlayers,
            center: true,
          },
          { data: mode },
          { data: userName, color: userColor },
          { data: message },
          {
            button: true,
            data: 'Join',
            center: true,
            callback: () => {
              socket.emit('joinRoom', id);
              console.log(id);
            },
          },
        ];
      }
    );

  const router = useRouter();
  const dispatch = useDispatch();

  return (
    <StyledRooms>
      <Header>
        <p
          style={{
            fontSize: '16px',
            fontWeight: 700,
            letterSpacing: '2px',
            marginLeft: '22px',
          }}
        >
          ACTIVE GAMES (4)
        </p>
      </Header>
      <TableContainer>
        <Table
          widthCells={[15, 15, 15, 45, 5]}
          dataPrimary={dataPrimary}
          rowsData={rowsData}
        />
      </TableContainer>
      <Button
        onClick={() => {
          socket.emit('createRoom');
        }}
      >
        Create new game
      </Button>
    </StyledRooms>
  );
};

export default Rooms;
