import styled from 'styled-components';

import Backdrop from '../../Backdrop';
import Button from '../../Button';

const ChatContainer = styled.div`
  position: relative;
  padding: 0 24px;
`;
const Cross = styled.img`
  position: fixed;
  top: 24px;
  right: 24px;
`;

const MessagesWindow = styled.div`
  margin-top: 70px;
  width: 100%;
  height: 55vh;

  overflow-x: hidden; /* Hide horizontal scrollbar */
  overflow-y: scroll; /* Add vertical scrollbar */
`;

const MessageContainer = styled.div``;

const Sender = styled.p`
  /* display: inline; */
  font-size: 18px;
  font-weight: 700;
  letter-spacing: 1px;
  color: #00fff0;
  line-height: 27px;
`;
const Message = styled.p`
  display: inline;
  font-style: normal;
  font-weight: bold;
  font-size: 18px;
  line-height: 27px;
`;

const Input = styled.input`
  width: 100%;
  height: 40px;
  margin: 0;
  padding: 0;
  font-size: 18px;
  background: transparent;
  outline: none;
  border: 1px solid #fff;
`;

const Chat = () => {
  return (
    <Backdrop>
      <ChatContainer>
        <Cross src="/images/cancel.svg" alt="cross-icon" />
        <MessagesWindow>
          <MessageContainer>
            {/* <Sender>123{'>'}</Sender> */}
            <Message>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Unde
              rerum harum modi non vero quasi, labore obcaecati earum ea enim
              accusamus quae aliquam reprehenderit possimus tempora ullam alias
              nam dolorum praesentium esse mollitia voluptates veniam. Nesciunt
              iste dolorem quia, cum qui reprehenderit aliquid ea libero
              doloremque natus commodi optio earum.
            </Message>
          </MessageContainer>
        </MessagesWindow>
        <Input />
        <Button style={{ width: '100%', height: 40 }}>SEND</Button>
      </ChatContainer>
    </Backdrop>
  );
};

export default Chat;
