import { useState } from 'react';
import styled from 'styled-components';

import { HuePicker } from 'react-color';

const SettingsSpaceShipContainer = styled.div`
  width: 90%;
  margin: 8px auto 0;

  border: 2px solid #ffffff;
  border-radius: 10px;
`;

const PointerColorPicker = styled.div`
  width: 25px;
  height: 25px;
  background: #fff;
  border-radius: 50%;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.35);
  transform: translate(-12.5px, 2.5px);
`;

const ContainerConfig = styled.div`

` 

const SettingsSpaceShip = () => {
  const [color, setColor] = useState('green');

  return (
    <>
      <p
        style={{
          fontSize: 18,
          fontWeight: 700,
          color: '#FFC700',
          textAlign: 'center',
          letterSpacing: 1,
        }}
      >
        Your spaceship:
      </p>
      <SettingsSpaceShipContainer>
        <p
          style={{
            margin: "8px 0 14px" ,
            fontSize: 16,
            fontWeight: 700,
            textAlign: 'center',
            letterSpacing: 1,
            textTransform: 'uppercase',
          }}
        >
          settings
        </p>
        <HuePicker
          width="100%"
          height="30px"
          pointer={PointerColorPicker}
          color={color}
          onChange={(color) => {
            setColor(color);
          }}
        ></HuePicker>
      </SettingsSpaceShipContainer>
    </>
  );
};

export default SettingsSpaceShip;
