/* imports */
import { useState } from 'react';
import styled from 'styled-components';
/* components */
import Backdrop from '../../Backdrop';
import Hue from '../../Hue';
/* redux */
import { useDispatch, useSelector } from 'react-redux';
import { hid } from '../../../../redux/actions/Modals';
import { onUser } from '../../../../redux/actions/User';
import { onUsers } from '../../../../redux/actions/Users';
import { socket } from '../../../Socket';

const Window = styled.div`
  position: fixed;

  transform: translate(-50%, -50%);
  top: 50%;
  left: 50%;
  z-index: 100;

  width: 400px;

  border: solid 3px #fff;
  border-radius: 5px;
  box-shadow: 0 0 15px 7px rgba(255, 255, 255, 0.15);
  background: rgba(0, 0, 0, 0.6);
`;

const Container = styled.div`
  width: 70%;
  margin: 0 auto;
`;

const Message = styled.p`
  margin-top: 20px;
  font-size: 16px;
  text-align: center;
  font-weight: 700;
`;

const Input = styled.input`
  width: 100%;
  height: 45px;
  border: 1px solid rgba(255, 255, 255, 0.8);
  border-radius: 8px;
  background: transparent;
  padding-left: 20px;

  color: ${({ color }) => color};
  font-size: 16px;
`;

const Submit = styled.button`
  display: block;
  margin: 20px auto;
  width: 85px;
  height: 45px;
  border: 1px solid rgba(255, 255, 255, 0.8);
  border-radius: 8px;
  background: transparent;
  font-size: 18px;
`;

const ModalInput = ({ message, submitText, color }) => {
  const [textColor, setTextColor] = useState('#fff');
  const [userName, setUserName] = useState('');

  const dispatch = useDispatch();
  const showModalInputUserCondition = useSelector(
    (state) => state.modalInputUser.show
  );

  const sendDataHandler = () => {
    socket.emit('user', { userName, textColor: textColor.hex });
    socket.on('user', (user) => {
      dispatch(onUser(user));
      dispatch(hideModalInputUser());
      console.log(user);
    });
    socket.on('users', (users) => {
      console.log(users);
      dispatch(onUsers(users));
    });
  };
  return (
    <>
      <Backdrop />

      <Window>
        <Container>
          <Message>{message}</Message>
          <Input
            onChange={(e) => {
              setUserName(e.target.value);
            }}
            color={textColor.hex}
          />
          {color && (
            <Hue
              onChange={(color) => {
                setTextColor(color);
              }}
              style={{ marginTop: 20 }}
              color={textColor}
            />
          )}
          <Submit
            onClick={() => {
              sendDataHandler(name);
            }}
          >
            {submitText}
          </Submit>
        </Container>
      </Window>
    </>
  );
};

export default ModalInput;
