import styled from "styled-components";

const RowContainer = styled.div`
  width: 100%;
  height: ${({ primary }) => primary};

  display: flex;
  justify-content: flex-start;
`;

const Cell = styled.div`
  width: ${({ width }) => width}%;
  height: 100%;

  display: flex;
  align-items: center;
`;

const Row = ({ primary, dataPrimary, widthCells, rowData }) => {
  return (
    <>
      {" "}
      {primary ? (
        <>
          <RowContainer>
            {dataPrimary.map(({ data }, index) => {
              if (data) return <Cell key={index} width ></Cell>;
            })}
          </RowContainer>
        </>
      ) : (
        <></>
      )}{" "}
    </>
  );
};

export default Row;
