/* imports */
import React from 'react'
import { useEffect } from 'react'
import styled from 'styled-components'
/* redux */
import { useSelector } from 'react-redux'
/* socketio */
import { socket } from '../components/Socket'
const Background = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  background: #000;
`
const Text = styled.div``
/* unity webgl */
import dynamic from 'next/dynamic'
// if (typeof window !== 'undefined') {
const Unity = dynamic({
  loader: () => import('luxon'),
  render(props, luxon) {
    console.log(luxon.DateTime);
    return <div>hello</div>;
  },
});

// }

const Game = () => {
  const idRoom = useSelector((state) => state.room.roomId)

  useEffect(() => {
    socket.emit('startGame', idRoom)
    socket.on('startLoadGame', (idGame) => {
      console.log(idGame)
    })
  }, [])

  return (
    <Background>
      {/* <Text>Game</Text> */}
      <Unity></Unity>
    </Background>
  )
}

export default Game
