import styled from 'styled-components';

const Container = styled.div`
  width: 45%;
  display: inline-block;
`;

const BarContainer = styled.div`
  height: 40px;
  background: rgba(177, 177, 177, 0.5);
  display: flex;
  justify-content: flex
  /* box-shadow: 0 0 15px #ff000080; */
`;

const BarContainerFill = styled.div`
  width: 50%;
  height: 40px;
  background: #ff0000;
  box-shadow: 0 0 15px #ff000080;

  justify-self: end;
`;

const Bar = ({
  style,
  name,
  value,
  maxValue,
  fillColor,
  alignLeft,
  alignRight,
}) => {
  return (
    <Container style={style}>
      <BarContainer>
        <BarContainerFill alignLeft={alignLeft} alignRight={alignRight} />
      </BarContainer>
    </Container>
  );
};

export default Bar;
