import { ADD_MESSAGE, ON_MESSAGES, SHOW_MODAL_MOBILE_CHAT, HIDE_MOBILE_CHAT } from '../actiontypes';

export function addMessage(message) {
  return {
    type: ADD_MESSAGE,
    payload: message,
  };
}

export function onMessages(messages) {
  return {
    type: ON_MESSAGES,
    payload: messages,
  };
}
