/* imports */
import React, { Component } from 'react'
import { useEffect } from 'react'
import styled from 'styled-components'
/* redux */
import { useSelector } from 'react-redux'
/* socketio */
import { socket } from '../components/Socket'
const Background = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  background: #000;
`
const Text = styled.div``
/* unity webgl */

import Unity, { UnityContext } from 'react-unity-webgl'

const Game = () => {
  const roomId = useSelector((state) => state.room.roomConfig.roomId)

  const unityContext = new UnityContext({
    dataUrl: 'test3/Build/test3.data',
    frameworkUrl: 'test3/Build/test3.framework.js',
    loaderUrl: 'test3/Build/test3.loader.js',
    codeUrl: 'test3/Build/test3.wasm',
  })

  unityContext.on('progress', (loadingStatus) => {
    socket.emit('loadingGameProcess', { idRoom: roomId, loadingStatus })
    console.log(`${loadingStatus} - loading...`)
  })

  unityContext.on('loaded', () => {
    socket.emit('gameLoaded', roomId)
  })

  socket.on('game-MultipleSpawn', (jsonData) => {
    unityContext.send(
      'Global',
      'Call',
      '{"spawns":[{"typeCall":"spawn","instantiate":{"entityData":{"type":"playerJoystick","id":"f4fjkd1dj3d4","entityName":"arrow","nickName":"lol228322","team":"1","color":"#aa23aa"},"positionData":{"position":{"x":1,"y":1},"rotate":{"deg":180}}}},{"typeCall":"spawn","instantiate":{"entityData":{"type":"playerJoystick","id":"32fjkd1dj3d4","entityName":"arrow","nickName":"test2","team":"1","color":"#a323aa"},"positionData":{"position":{"x":1,"y":-1},"rotate":{"deg":45}}}},{"typeCall":"spawn","instantiate":{"entityData":{"type":"playerJoystick","id":"4afjkd1dj3d4","entityName":"arrow","nickName":"test3","team":"1","color":"#aa23aa"},"positionData":{"position":{"x":-1,"y":-1},"rotate":{"deg":34}}}}]}',
    )
    console.log(jsonData)
  })

  socket.on('game-joystickController1', (jsonData) => {
    const { id } = JSON.parse(jsonData)
    console.log(jsonData)
    unityContext.send(`playerJoystick-${id}`, 'Call', jsonData)
  })

  return (
    <Background>
      <Unity width="70%" unityContext={unityContext}></Unity>
    </Background>
  )
}

export default Game
