import styled from "styled-components";

const RowContainer = styled.div`
  width: 100%;
  height: ${({ primary }) => primary};

  display: flex;
  justify-content: flex-start;
`;

const Cell = styled.div`
  width: ${({ width }) => width}%;
  height: 100%;

  display: flex;
  align-items: center;
`;

const Data = styled.div`
  font-size: 14px;
  color: #fff;
  font-weight: 700;
`;

const Button = styled.div``;

const Row = ({ primary, dataPrimary, widthCells, rowData }) => {
  return (
    <>
      {" "}
      {primary ? (
        <>
          <RowContainer>
            {dataPrimary.map(({ data }, index) => {
              if (data)
                return (
                  <Cell key={index} width={widthCells[index]}>
                    {data}
                  </Cell>
                );
            })}
          </RowContainer>
        </>
      ) : (
        <>
          <RowContainer>
            {dataPrimary.map(({ data, button }, index) => {
              if (data && !button)
                return (
                  <Cell key={index} width={widthCells[index]}>
                    {data}
                  </Cell>
                );
              else if (data && button) {
                return (
                  <Cell key={index} width={widthCells[index]}>
                    {data}
                  </Cell>
                );
              }
            })}
          </RowContainer>
        </>
      )}{" "}
    </>
  );
};

export default Row;
