import styled from 'styled-components';

const Container = styled.div`
  width: 45%;
  display: inline-block;
  background: green;
`;

const BarContainer = styled.div`
  height: 40px;
  background: #b1b1b1;
`;

const BarContainerFill = styled.div`
  width: 50%;
  height: 40px;
  background: #ff0000;
  box-shadow: 0 0 15px #ff000060;
`;

const Bar = ({
  style,
  name,
  value,
  maxValue,
  fillColor,
  alignLeft,
  alignRight,
}) => {
  return (
    <Container style={style}>
      <BarContainer>
        <BarContainerFill />
      </BarContainer>
    </Container>
  );
};

export default Bar;
