import styled from 'styled-components';

import Backdrop from '../../Backdrop';

const ChatContainer = styled.div`
  position: relative;
  padding: 0 24px;
`;
const Cross = styled.img`
  position: fixed;
  top: 24px;
  right: 24px;
`;

const MessagesWindow = styled.div`
  margin-top: 70px;
  width: 100%;
  height: 65vh;
  background: red;

  overflow-x: hidden; /* Hide horizontal scrollbar */
  overflow-y: scroll; /* Add vertical scrollbar */
  margin: 0;
`;

const Input = styled.input`
    margin: 0;
    padding:0;
`

const Chat = () => {
  return (
    <Backdrop>
      <ChatContainer>
        <Cross src="/images/cancel.svg" alt="cross-icon" />
        <MessagesWindow>
          Lorem ipsum dolor, sit amet consectetur adipisicing elit. Nulla,
          tempora fuga cupiditate provident incidunt maiores, voluptatem ipsum
          exercitationem corporis cum est voluptas ratione tempore nesciunt
          aspernatur quod nemo omnis? Voluptate, consequuntur veritatis. At odio
          pariatur corporis? Neque nam molestias non asperiores, corrupti
          reiciendis aliquam dolor, illo doloremque mollitia cum at.
          <input style={{ fontSize: '16px' }} type="text" />
        </MessagesWindow>
        <Input />
      </ChatContainer>
    </Backdrop>
  );
};

export default Chat;
